const gulp=require("gulp"),cleanCSS=require("gulp-minify-css"),htmlmin=require("gulp-htmlmin"),minify=require("gulp-minify");gulp.task("minify-html",function(){return gulp.src("./*.html").pipe(htmlmin({collapseWhitespace:!0})).pipe(gulp.dest("./"))}),gulp.task("minify-css",()=>gulp.src("./*.css").pipe(cleanCSS({compatibility:"ie8"})).pipe(gulp.dest("./"))),gulp.task("compress",function(){gulp.src(["./*.js"]).pipe(minify()).pipe(gulp.dest("./"))}),gulp.task("default",gulp.series(["minify-css","minify-html","compress"]));